/* Aplicado*/
alter table ventas add column clientes_id int unsigned default 0;

/*nuevo*/
CREATE TABLE IF NOT EXISTS venta_creditos(
    id BIGINT UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    ventas_id BIGINT UNSIGNED,
    fecha_abono TIMESTAMP DEFAULT NOW(),
    abono DOUBLE,
    status BOOLEAN DEFAULT 1,
    turnos_id BIGINT UNSIGNED,
    forma VARCHAR(30)
)ENGINE=INNODB;

